import React, { useState } from "react";
import axios from "axios";

export default function QueryInput({ selectedSheet, fileName, setResult }) {
  const [query, setQuery] = useState("");

  const handleQuery = async () => {
    if (!query || !selectedSheet || !fileName) return;

    try {
      const res = await axios.post(`${process.env.REACT_APP_API_URL}/query`, null, {
        params: { query, sheet: selectedSheet, filename: fileName }
      });
      setResult(res.data.response);
    } catch (err) {
      setResult("Error processing query");
      console.error(err);
    }
  };

  return (
    <div>
      <textarea
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Ask a question..."
      />
      <button onClick={handleQuery}>Ask</button>
    </div>
  );
}
