// components/QueryInput.js
import React, { useState } from "react";
import { askQuery } from "../services/api";

export default function QueryInput({ fileId, onResult }) {
  const [q, setQ] = useState("");
  const [loading, setLoading] = useState(false);

  const run = async () => {
    if (!q) return alert("Type a question");
    setLoading(true);
    try {
      const res = await askQuery(fileId, q);
      onResult(res);
    } catch (e) {
      alert("Query failed: " + (e.response?.data?.detail || e.message));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <input
        type="text"
        value={q}
        onChange={(e)=>setQ(e.target.value)}
        placeholder="Ask something about your data (e.g., top 5 products by revenue)"
        style={{ width: "60%" }}
      />
      <button onClick={run} disabled={loading} style={{ marginLeft: 10 }}>
        {loading ? "Thinking..." : "Ask"}
      </button>
    </div>
  );
}
