import React, { useState } from "react";
import { sendQuery } from "../services/api";

export default function QueryInput({ selectedSheet, fileName, setResult }) {
  const [query, setQuery] = useState("");

  const handleQuery = async () => {
    if (!query || !selectedSheet) return;
    const data = await sendQuery(query, selectedSheet, fileName);
    setResult(data.response);
  };

  return (
    <div>
      <textarea onChange={(e) => setQuery(e.target.value)} placeholder="Ask a question..." />
      <button onClick={handleQuery}>Ask</button>
    </div>
  );
}
