// components/ResultDisplay.js
import React from "react";
import { Chart } from "react-chartjs-2";
import {
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend,
  Chart as ChartJS
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, LineElement, ArcElement, Tooltip, Legend);

export default function ResultDisplay({ data }) {
  if (!data) return null;

  const llm = data.llm || {};
  const table = data.table || llm.table;
  const chartInstr = data.chart || llm.chart;

  return (
    <div style={{ marginTop: 20 }}>
      <h3>Answer</h3>
      <div style={{ whiteSpace: "pre-wrap", background: "#fafafa", padding: 12 }}>{llm.answer_text || JSON.stringify(llm, null, 2)}</div>

      {table && (
        <div style={{ marginTop: 16 }}>
          <h4>Table</h4>
          <table border="1" cellPadding="6" cellSpacing="0">
            <thead>
              <tr>{table.columns.map((c) => <th key={c}>{c}</th>)}</tr>
            </thead>
            <tbody>
              {table.rows.map((r, i) => (
                <tr key={i}>
                  {table.columns.map((c) => <td key={c}>{String(r[c] ?? "")}</td>)}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {chartInstr && chartInstr.type && (
        <div style={{ marginTop: 16 }}>
          <h4>Chart</h4>
          <Chart
            type={chartInstr.type === "bar" ? "bar" : chartInstr.type === "line" ? "line" : "pie"}
            data={{
              labels: chartInstr.data.map(d => d.x),
              datasets: [{ label: chartInstr.label || chartInstr.y, data: chartInstr.data.map(d => d.y) }]
            }}
            options={{ responsive: true }}
          />
        </div>
      )}
    </div>
  );
}
